        private void StripQuestions(string s,bool TrimHtml)
        {
            string[] quests_Divs = new string[] { };
           bool is_25 = !TrimHtml;

            try
            {
                string s1 = "<div id=\"common_question_container\">";
                string s2 = "<div class=\"questionBox-borderNone\" id=\"noQuestionBox_profile\" style=\"display:none\">";


                if (TrimHtml)
                {
                    s = Ques_.TrimeHTML(s);
                s1="$(\".questionBox:last\").after(\"<script type=\\\"text/javascript\\\">//<![CDATA[  App.initQuestionBoxes();//]]></script>";
                s2 = "class=\\\"questionBox\\\" id=\\";


                }/* bool con1 = s.Contains(s1);
                bool con2 = s.Contains(s2);

                if (!con1 || !con2)
                    return;
                */
                string[] piecs = s.Split(new string[] { s1, s2 }, StringSplitOptions.RemoveEmptyEntries);
                // <div class=\"questionBox\"

                if (!TrimHtml)
                {
                    if (piecs.Length < 1)
                        return;
                    string quest_string = piecs[1];
                    if (quest_string.Length < 99)
                        return;

                    quest_string = quest_string.Substring(97).Trim();

                    quests_Divs = quest_string.Split(new string[] { "<div class=\"questionBox\"" }, StringSplitOptions.RemoveEmptyEntries);

                }
                else quests_Divs = piecs;
                foreach (string q in quests_Divs)
                {
                    if (q.Trim() == "" || q == "  <div ")
                        continue;


                    Ques_ qui = Ques_.Create(Ques_.TrimeHTML(q), this._Quests.Count,is_25);
                    AddNewQuest(qui); 
                }
            }
            catch { }
        }

        private void AddNewQuest(Ques_ qui)
        {
            try
            {
                foreach (Ques_ q in this._Quests)
                    if (q.ID == qui.ID)
                        return;
                if (this._Quests.Count < this._CurrentAnswers)
                    this._Quests.Add(qui);
            }
            catch { }
        }
