        private Image LoadCover()
        {
            try
            {
                if (this.DonotLoadImages())
                    return null;
                if (this.CurrentBG.IsUrl())
                {
                    var request = System.Net.WebRequest.Create(this.CurrentBG);

                    using (var response = request.GetResponse())
                    using (var stream = response.GetResponseStream())
                    {
                        return Bitmap.FromStream(stream);
                    }
                }
                return null;
            }
            catch { return null; }
        }

