        private void CheckAccountIfno(string src)
        {
            string[] tags = src.Split(new char [] {'<'});
            foreach(string t in tags)
            {
                if (t.Contains("title>"))
                {
                    string[] titlePeieces = t.Split(new char[] {'|' });
                   
                    if(t.Contains("|"))
                    this._CurrentName = titlePeieces[0].Substring(6);
                } //<span class="stasis-digit" id="profile_gifts_counter">7</span>
                else if (t.Contains("span class=\"stasis-digit\" id=\"profile_gifts_counter")) 
                {
                    string[] GitfsPieces = t.Split(new char[] { '>' });
                    string giftCount = GitfsPieces[1].Trim();
                    if (giftCount.isNum())
                        this._CurrenGifts = int.Parse(giftCount);

                }//<span class="stasis-digit" id="profile_liked_counter">6670
                else if (t.Contains("span class=\"stasis-digit\" id=\"profile_liked_counter\">"))
                {
                    string[] LikesPieces = t.Split(new char[] { '>' });
                    string LikesCount = LikesPieces[1].Trim();
                    if (LikesCount.isNum())
                        this._CurrentLikes = int.Parse(LikesCount);
                }//<span class="stasis-digit" id="profile_answer_counter">116</span>
                else if (t.Contains("span class=\"stasis-digit\" id=\"profile_answer_counter"))
                {
                    string[] ANS_Pieces = t.Split(new char[] { '>' });
                    string Ans_Count = ANS_Pieces[1].Trim();
                    if (Ans_Count.isNum())
                        this._CurrentAnswers = int.Parse(Ans_Count);
                }
                // <img alt="Yaser @sandboxer" id="profile-picture" src="http://limg2.ask.fm/assets2/100/083/112/704/thumb/beckham.jpg" /></a>
                else if (t.Contains("id=\"profile-picture\" src="))
                {
                    string[] Ava_Pieces = t.Split(new char[] { '"' });
                    if (Ava_Pieces.Length > 5)
                    {
                        string Ava_Count = Ava_Pieces[5].Trim();
                        this.CurrentAvatar = Ava_Count;
                    }
                }
                    //html{background-repeat:repeat;background-position:left top;background-image:url(http://limg2.ask.fm/wallpapers2/053/416/829/440/original/Gogler.jpg?1431755469);background-color:#264C67;background-attachment:fixed;}
                else if(t.Contains("background-image:url"))
                {
                    string[] BG_Pieces = t.Split(new char[] { ')','(' });
                    if (BG_Pieces.Length > 1)
                    {
                        string BG_Count = BG_Pieces[1].Trim();
                        this.CurrentBG = BG_Count;
                    }
                }
            }
        }

