        private void SearchFor(string p)
        {
            int i=-1; 
            _searchLst.Clear();
            foreach(Ques_ q in this._Quests)
            {
                i++;
                if(q.Contains(p))
                    _searchLst.Add(i);

            }
            if (_searchLst.Count > 0)
            {
                this._currentSearchIndex = 0;
                this._CurrentIndex = this._searchLst[0];
                DisplayQuest(this._Quests[this._CurrentIndex]);
            }

            labelSearchStatue.Text = "";
            foreach(int ixn in _searchLst)
                labelSearchStatue.Text += (ixn+1).ToString()+" . ";
        }


        public List<int> _searchLst = new List<int>();

