        private void NextSearch()
        {
            this._currentSearchIndex++;
            if (_currentSearchIndex > _searchLst.Count - 1)
                _currentSearchIndex = 0;

            this._CurrentIndex = _searchLst[this._currentSearchIndex];


            DisplayQuest(this._Quests[this._CurrentIndex]);

        }

